import React from "react";
import { Container, Row, Button, Table } from "reactstrap";
import { Link } from "react-router-dom";
import CommonSection from "../shared/CommonSection";

import "./home.css";
import SearchBar from "../shared/SearchBar";
import Newsletter from "../shared/Newsletter";

import useFetch from "../hooks/useFetch";
import { BASE_URL } from "../utils/config";

const AdminHome = () => {
  const { data: users, loading, error } = useFetch(`${BASE_URL}/users`);

  const deleteUser = async (_id) => {
    try {
      const res = await fetch(`${BASE_URL}/users/${_id}`, {
        method: "DELETE",
      });
      console.log(res);
      const result = res.json();
      if (!res.ok) alert(result.message);

      console.log(result.data);
    } catch (error) {
      alert(error.message);
    }
  };
  return (
    <>
      <CommonSection title={"Ong Sang Tours"} />
      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>

      <section>
        <Container>
          <Row className="navbar__admin">
            <Button className="d-flex w-25 text font-weight-bold">
              <Link to="/admin" className="text-white">
                Users Management
              </Link>
            </Button>
            <Button className="d-flex w-25 text font-weight-bold">
              <Link to="/admin/tours" className="text-white">
                Tours Management
              </Link>
            </Button>
            <Button className="d-flex w-25 text font-weight-bold ">
              <Link to="/admin/reviews" className="text-white">
                Reviews Management
              </Link>
            </Button>
          </Row>
        </Container>
      </section>

      <Container>
        <Button>
          <Link to="/admin/add-user" className="text-white">
            Add User
          </Link>
        </Button>
      </Container>

      <section className="pt-0">
        <Container>
          {loading && <h4 className="text-center pt-5">Loading...</h4>}
          {error && <h4 className="text-center pt-5">{error}</h4>}
          {!loading && !error && (
            <Table striped>
              <thead>
                <tr>
                  <th>STT</th>
                  <th>Full Name</th>
                  <th>Email</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users?.map((user, index) => (
                  <tr key={index}>
                    <td>{index + 1}</td>
                    <td>{user.username}</td>
                    <td>{user.email}</td>
                    <td>
                      <Link to={`/admin/update/${user._id}`}>
                        <button
                          style={{
                            padding: "3px",
                            marginRight: "10px",
                            color: "blue",
                          }}
                        >
                          <i className="ri-pencil-fill"></i>
                        </button>
                      </Link>
                      <button
                        onClick={() => {
                          deleteUser(`${user._id}`);
                        }}
                        style={{ padding: "3px", color: "red" }}
                      >
                        <i className="ri-delete-bin-fill"></i>
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Container>
      </section>
      <Newsletter />
    </>
  );
};

export default AdminHome;
