import React, { useContext } from "react";
import { Routes, Route, Navigate } from "react-router-dom";

import AdminHome from "../admin/Home";
import UpdateUser from "../admin/handleUsers/UpdateUser";
import AddUser from "../admin/handleUsers/AddUser";

import Home from "./../pages/Home";
import Tours from "./../pages/Tours";
import TourDetails from "./../pages/TourDetails";
import Login from "./../pages/Login";
import Register from "./../pages/Register";
import SearchResultList from "./../pages/SearchResultList";
import ThankYou from "../pages/ThankYou";

import { AuthContext } from "./../context/AuthContext";

const Routers = () => {
  return (
    <Routes>
      <Route
        path="/admin"
        element={
          <ProtectedRouteAdmin>
            <AdminHome />
          </ProtectedRouteAdmin>
        }
      />
      <Route
        path="/admin/update/:id"
        element={
          <ProtectedRouteAdmin>
            <UpdateUser />
          </ProtectedRouteAdmin>
        }
      />
      <Route
        path="/admin/add-user"
        element={
          <ProtectedRouteAdmin>
            <AddUser />
          </ProtectedRouteAdmin>
        }
      />

      <Route
        path="/"
        element={
          <ProtectedRoute>
            <Navigate to="/home" />
          </ProtectedRoute>
        }
      />
      <Route
        path="/home"
        element={
          <ProtectedRoute>
            <Home />
          </ProtectedRoute>
        }
      />
      <Route
        path="/tours"
        element={
          <ProtectedRoute>
            <Tours />
          </ProtectedRoute>
        }
      />
      <Route
        path="/tours/:id"
        element={
          <ProtectedRoute>
            <TourDetails />
          </ProtectedRoute>
        }
      />
      <Route
        path="/login"
        element={
          <ProtectedRoute>
            <Login />
          </ProtectedRoute>
        }
      />
      <Route
        path="/register"
        element={
          <ProtectedRoute>
            <Register />
          </ProtectedRoute>
        }
      />
      <Route
        path="/thank-you"
        element={
          <ProtectedRoute>
            <ThankYou />
          </ProtectedRoute>
        }
      />
      <Route
        path="/tours/search"
        element={
          <ProtectedRoute>
            <SearchResultList />
          </ProtectedRoute>
        }
      />
    </Routes>
  );
};

const ProtectedRouteAdmin = ({ children }) => {
  const { user } = useContext(AuthContext);
  if (user && user.role === "admin") {
    return children;
  }
  console.log(user);
  return <Navigate to="/" replace />;
};

const ProtectedRoute = ({ children }) => {
  const { user } = useContext(AuthContext);
  if (!user || user.role === "user") {
    return children;
  }
  console.log(user);
  return <Navigate to="/admin" replace />;
};

export default Routers;
